import React, {useState} from 'react';
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpenseFilter.js";
import Card from '../UI/Card';
import './NewExpenses.css';

function NewExpenses(props) {

  const [filteredYear, setFilteredYear] = useState('0');

  const [filteredExpenseList, setFilteredExpenseList] = useState(props.expenses);

  const filterChangeHandler = (yearSelected) => {
    console.log(yearSelected);
    setFilteredYear(yearSelected);
    console.log(filteredYear);
    if(filteredYear != '0')
      setFilteredExpenseList(props.expenses.filter(exp => exp.date.getFullYear() == filteredYear));
  };

  return (
    <div>
      <Card className="expenses">
      <ExpensesFilter selected={filteredYear} onYearSelected={filterChangeHandler}></ExpensesFilter>
      {filteredExpenseList.map((expense) => (
        console.log(expense.date.getFullYear()),
      <ExpenseItem
        key={expense.id} //unique identifier
        title={expense.title}
        amount={expense.amount}
        date={expense.date}>
      </ExpenseItem>))}
    </Card>
    </div>
    
  );
}

export default NewExpenses;
